const Styled = styled.div`
  display: flex;
  align-items: center;
  svg path {
    fill: ${({ color }) => color || undefined};
  }
  svg {
    width: ${({ width }) => width || undefined};
    height: ${({ height }) => height || undefined};
  }
`;

function Icon({ name, color, width, height, ...props }) {
  if (!iconList[name]) return null;
  return (
    <Styled
      color={color}
      width={width}
      height={height}
      className="flex-centered"
      {...props}
    >
      {iconList[name]}
    </Styled>
  );
}

